// Get full digit history
const digits = data.digits;
const total = digits.length;

// Helper: Check if digit is ODD
const isOdd = (d) => d % 2 !== 0;

// Helper: Check if 'count' digits strictly match the end of the history
// AND the digit just before them does NOT match (ensures exact trigger)
const isStrictStreak = (count) => {
    if (total < count + 1) return false; // Need history + 1 previous digit

    // 1. Check if last 'count' digits are ALL Odd
    const streak = digits.slice(-count);
    const streakIsOdd = streak.every(isOdd);

    // 2. Check if the digit BEFORE the streak is EVEN (Strict Start)
    const prevDigit = digits[total - count - 1]; // The digit just before the streak
    const prevIsEven = !isOdd(prevDigit);

    return streakIsOdd && prevIsEven;
};

